<div id="add_product" class="modal fade" role="dialog" data-backdrop="static">	<div class="modal-dialog modal-md">		<div class="modal-content">			<div class="modal-header">				<button type="button" class="close" data-dismiss="modal">					&times;				</button>				<h4 class="modal-title">Report Fixed Stock </h4>			</div>			<div class="modal-body">				<div class="row">					<div class="col-md-6">						<div class="form-group">							<label for="date">Date<span class="validation-color">*</span></label>							<div class="input-group date">								<input type="text" class="form-control datepicker" id="invoice_date" name="invoice_date" value="" autocomplete="off">								<div class="input-group-addon">									<i class="fa fa-calendar"></i>								</div>							</div>							<span class="validation-color" id="err_date"></span>						</div>					</div>					<div class="col-md-6">						<div class="form-group">							<label for="">Reference Number<span class="validation-color">*</span></label>							<input type="text" class="form-control" id="" name="" value="">							<span class="validation-color" id="err_"></span>						</div>					</div>				</div>				<div class="row">					<div class="col-md-6">						<div class="form-group">							<label for="">Product<span class="validation-color">*</span></label>							<select class="from-control select2">								<option>Select Product</option>								<option value="">Computer</option>								<option value="">Mobile</option>								<option value="">Keyboard</option>							</select>							<span class="validation-color" id="err_product"></span>						</div>					</div>					<div class="col-md-6">						<div class="form-group">							<label for="">Quantity<span class="validation-color">*</span></label>							<input type="number" class="form-control" id="" name="" value="">							<span class="validation-color" id="err_"></span>						</div>					</div>				</div>				<div class="row">					<div class="col-md-6">						<div class="form-group">							<label for="category_type_a">Movement Type<span class="validation-color">*</span></label>							<select class="form-control select2" id="" name="">								<option value="">Select Movement Type</option>								<option value='damaged'>Damaged</option>								<option value='missing'>Missing</option>							</select>							<span class="validation-color" id="err_category_type_a"></span>						</div>					</div>					<div class="col-md-6">						<div class="form-group">							<label for="">Attachment <span class="validation-color"></span></label>							<input type="file" class="form-control" id="" name="" value="">							<span class="validation-color" id="err_"></span>						</div>					</div>					<div class="col-md-12">						<div class="form-group">							<label for="">Comments<span class="validation-color">*</span></label>							<textarea name="Name" class="form-control" rows="2" cols="40"></textarea>							<span class="validation-color" id="err_"></span>						</div>					</div>				</div>			</div>			<div class="modal-footer">				<button type="submit" id="category_add_submit" class="btn btn-info" class="close" data-dismiss="modal">					Add				</button>				<button type="submit" class="btn btn-info" class="close" data-dismiss="modal">					Cancel				</button>			</div>		</div>	</div></div>