   {"data": "date"},                // { "data": "invoice" },                {"data": "customer"},                {"data": "grand_total"},                {"data": "currency_converted_amount"},                {"data": "paid_amount"},                {"data": "payment_status"},                {"data": "pending_amount"},                {"data": "added_user"},            ]        });        $("#post_notification_date").click(function (e) {            e.preventDefault();            var sales_id = $('#salesId').val();            var sales_type = $('#type').val();            var update_date = $('#invoice_date').val();            var comments = $('#comments').val();            if (update_date == null || update_date == "")            {                $('#err_date').text('please Select Date');                return false;            }            $.ajax({                url: base_url + "follow_up/follow_up",                type: "POST",                data: {'sales_id': sales_id, 'sales_type': sales_type, 'update_date': update_date, 'comments': comments},                success: function (data) {                    var obj = JSON.parse(data);                    if (obj.status = 'success')                    {                        $("#myModal2").modal('show');                    }                }            })        });    });    $('body').on('change', 'input[type="checkbox"][name="check_item"]', function () {        var i = 0;        $.each($("input[name='check_item']:checked"), function () {            i++;        });        if (i == 1)        {            var row = $("input[name='check_item']:checked").closest("tr");            var action_button = row.find('.action_button').html();            console.log(action_button);            $('#plus_btn').hide();            $('.filter_body').html(action_button);            $('#filter').show();        } else        {            $('#plus_btn').show();            $('#filter').hide();            $('.filter_body').html('');        }    });    function addToModel(id) {        // var id = data;        document.getElementById("salesId").value = id;        $.ajax({            type: "GET",            url: base_url + "follow_up/follow/" + id,            success: function (data)            {                // alert(data)                // $obj = JSON.parse(data);                $("#follow_up_table").html(data);            }        });    }</script>