<div id="add_tax" class="modal fade" role="dialog">    <div class="modal-dialog modal-md">        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h4 class="modal-title">Add Tax</h4>            </div>            <form name="form_tax" id="form_tax" method="post">                <div class="modal-body">                    <div id="loader">                        <h1 class="ml8"><span class="letters-container"> <span class="letters letters-left"><img src="<?php echo base_url('assets/'); ?>images/loader-icon.png" width="40px"></span></span><span class="circle circle-white"></span><span class="circle circle-dark"></span><span class="circle circle-container"><span class="circle circle-dark-dashed"></span></span></h1>                    </div>                    <div class="row">                        <div class="col-sm-6">                            <div class="form-group">                                <label for="tax_name"><!-- Tax Name --> Tax Type <span class="validation-color">*</span></label>                                <select class="form-control select2" id="tax_name" name="tax_name">                                    <option value="">Select Tax Type</option>                                    <option value="GST">GST</option>                                    <option value="TDS">TDS</option>                                    <option value="TCS">TCS</option>                                    <option value="CESS">CESS</option>                                </select>                                                                   <span class="validation-color" id="err_tax_name_add"><?php echo form_error('tax_name'); ?></span>                            </div>                        </div>                        <div class="col-sm-6" id="section">                            <div class="form-group">                                <label for="tax_type">Section <span class="validation-color">*</span></label>                                <select class="form-control select2" id="cmb_section" name="cmb_section">                                    <option value="">Select Section</option>                                    <?php foreach ($tax_section as $value) {                                        ?>                                        <option value="<?php echo $value->section_id; ?>"><?php echo $value->section_name; ?></option>                                        <?php                                    }                                    ?>                                </select>                                 <input type="hidden" class="form-control" id="tax_id" name="tax_id" value="0" >                                                                <span class="validation-color" id="err_tax_section_name"><?php echo form_error('tax_name'); ?></span>                            </div>                        </div>                                                </div>                    <div class="row">                        <div class="col-sm-6">                            <div class="form-group sales">                                <label for="tax_value">Tax Value in % <span class="validation-color">*</span></label>                                <div class="input-group">                                    <input type="text" min="0" max="100" class="form-control number text-right" id="tax_value" name="tax_value" value="<?php echo set_value('tax_value'); ?>" tabindex="2">                                    <span class="input-group-addon">%</span>                                </div>                                <span class="validation-color" id="err_tax_value"><?php echo form_error('tax_value'); ?></span>                            </div>                        </div>                        <div class="col-sm-6">                            <div class="form-group">                                <label for="description"><!-- Description --> Description</label>                                <textarea rows="2" class="form-control" id="description" name="description" tabindex="3"><?php echo set_value('description'); ?></textarea>                                <span class="validation-color" id="err_description"></span>                            </div>                        </div>                     </div>                 </div>                                <div class="modal-footer">                    <button type="button" id="add_tax_submit" class="btn btn-info">                        Add                    </button>                    <button type="button" class="btn btn-info" data-dismiss="modal">                        Cancel                    </button>                </div>            </form>        </div>    </div></div><script src="<?php echo base_url('assets/js/tax/') ?>tax.js"></script><script src="<?php echo base_url('assets/js/') ?>icon-loader.js"></script><script type="text/javascript">    $(document).on("change", "#tax_name", function () {        var val = $(this).val();        if (val == "CESS" || val == "GST") {            $('#cmb_section').val('').change();            $("#section").hide();        } else {            $("#section").show();        }            });    $('.number').keypress(function (event) {        var $this = $(this);        if ((event.which != 46 || $this.val().indexOf('.') != -1) &&                ((event.which < 48 || event.which > 57) &&                        (event.which != 0 && event.which != 8))) {            event.preventDefault();        }        var text = $(this).val();        if ((event.which == 46) && (text.indexOf('.') == -1)) {            setTimeout(function () {                if ($this.val().substring($this.val().indexOf('.')).length > 3) {                    $this.val($this.val().substring(0, $this.val().indexOf('.') + 3));                }            }, 1);        }        if ((text.indexOf('.') != -1) &&                (text.substring(text.indexOf('.')).length > 2) &&                (event.which != 0 && event.which != 8) &&                ($(this)[0].selectionStart >= text.length - 2)) {            event.preventDefault();        }    });        $('.number').bind("paste", function (e) {        var text = e.originalEvent.clipboardData.getData('Text');        if ($.isNumeric(text)) {            if ((text.substring(text.indexOf('.')).length > 3) && (text.indexOf('.') > -1)) {                e.preventDefault();                $(this).val(text.substring(0, text.indexOf('.') + 3));            }        } else {            e.preventDefault();        }    });</script>