        if (address == null || address == "") {            $("#err_address").text('Please Enter Address');            return false;        } else {            $("#err_address").text('');        }        if (country == null || country == "") {            $("#err_country").text("Please Select Country ");            return false;        } else {            $("#err_country").text("");        }        if (state == null || state == "") {            $("#err_state").text("Please Select State ");            return false;        } else {            $("#err_state").text("");        }        if (city == null || city == "") {            $("#err_city").text("Please Select City ");            return false;        } else {            $("#err_city").text("");        }        if (flag_value == 'Y' || flag_label == 'Y') {            alert_d.text ='Please Enter valid input for customer field and label';            PNotify.error(alert_d);             return false;        }    });    $('#vat').click(function () {        if ($(this).prop("checked") == true) {            $("#gst, #d_gst").prop("checked", false);            $("#gst, #d_gst").attr("disabled", true);        } else if ($(this).prop("checked") == false) {            $("#gst, #d_gst").attr("disabled", false);        }    });    $("#g_gst_items").hide();    $('#d_gst').click(function () {        if ($(this).prop("checked") == true) {            $("#g_gst_items").show();            $("#vat, #service_tax, #gst").prop("checked", false);            $("#vat, #service_tax, #gst").attr("disabled", true);        } else if ($(this).prop("checked") == false) {            $("#g_gst_items").hide();            $("#vat, #service_tax, #gst").attr("disabled", false);        }    });    $('#gst').click(function () {        if ($(this).prop("checked") == true) {            $("#vat, #service_tax, #d_gst").prop("checked", false);            $("#vat, #service_tax, #d_gst").attr("disabled", true);        } else if ($(this).prop("checked") == false) {            $("#vat, #service_tax, #d_gst").attr("disabled", false);        }    });</script>			<script type="text/javascript">    $(document).ready(function () {        var maxField = 10;        //Input fields increment limitation        var addButton = $('.add_button');        //Add button selector        var wrapper = $('.field_wrapper');        //New input field html        var x = 1;        var count = 1;        //Initial field counter is 1        //Once add button is clicked        $(addButton).click(function () {            //Check maximum number of input fields            var flag_label = 'N';            $(".custom_lablel").each(function () {                var lbl = $(this).val();                if (lbl == '') {                    flag_label = 'Y';                }            });            var flag_value = 'N';            $(".custom_field").each(function () {                var val_cu = $(this).val();                if (val_cu == '') {                    flag_value = 'Y';                }            });            if (flag_value == 'Y' || flag_label == 'Y') {                alert_d.text ='Please Enter valid input for customer field and label';                PNotify.error(alert_d);                return false;            } else {                if (x < maxField) {                    //Input field wrapper                    var fieldHTML = '<div class="col-sm-4 form-group"><label><input type="text" id="custom_lablel' + count + '" name="custom_lablel[]" Placeholder="Textfield" class="disable_in form-control custom_lablel" id="edit_label"></label><a href="javascript:void(0);" class="remove_button btn btn-info btn-xs pull-right" title="Remove"><i class="fa fa-minus"></i></a><input type="text" id="custom_field' + count + '" name="custom_field[]" value="" class="form-control custom_field"/></div>';                    x++;                    //Increment field counter                    count++;                    $(wrapper).append(fieldHTML);                    //Add field html                }            }        });        //Once remove button is clicked        $(wrapper).on('click', '.remove_button', function (e) {            e.preventDefault();            $(this).parent('div').remove();            //Remove field html            x--;            //Decrement field counter        });        $(wrapper).on('dblclick', '#edit_label', function () {            console.log("fdf");            $(this).removeClass('disable_in');        });        $("#gst_number").on("blur keyup", function (event) {            var gstin = $('#gst_number').val();            $("#gst_number").val(gstin.toUpperCase());            var gstin_length = gstin.length;            if (gstin_length > 0)            {                if (gstin == null || gstin == "") {                    $("#err_gstin").text("Please Enter GSTIN Number.");                    return false;                } else {                    $("#err_gstin").text("");                }                if (!gstin.match(sname_regex)) {                    $('#err_gstin').text("Please Enter Valid GSTIN Number.");                    return false;                } else {                    $("#err_gstin").text("");                }                if (gstin_length < 15 || gstin_length > 15)                {                    $("#err_gstin").text("GSTIN Number should have length 15");                    return false;                } else {                    $("#err_branch_gstin_number").text("");                }            }        });    });    $('#cmb_country').change(function () {        var id = $(this).val();        $('#cmb_state').empty();        $('#cmb_city').empty();        $.ajax({            url: base_url + 'general/get_state/',            method: "POST",            data: {id: id},            dataType: "JSON",            success: function (data) {                console.log(data);                var state = $('#cmb_state');                var opt = document.createElement('option');                opt.text = 'Select State';                opt.value = '';                state.append(opt);                var city = $('#cmb_city');                var opt1 = document.createElement('option');                opt1.text = 'Select City';                opt1.value = '';                city.append(opt1);                for (i = 0; i < data.length; i++) {                    $('#cmb_state').append('<option value="' + data[i].state_id + '">' + data[i].state_name + '</option>');                }            }        });    });    $('#cmb_state').change(function () {        var id = $(this).val();        $('#cmb_city').empty();        $.ajax({            url: base_url + 'general/get_city/' + id,            dataType: "JSON",            success: function (data) {                console.log(data);                var city = $('#cmb_city');                var opt = document.createElement('option');                opt.text = 'Select City';                opt.value = '';                city.append(opt);                for (i = 0; i < data.length; i++) {                    $('#cmb_city').append('<option value="' + data[i].city_id + '">' + data[i].city_name + '</option>');                }                var opt1 = document.createElement('option');                opt1.text = 'Others';                opt1.value = '0';                city.append(opt1);            }        });    });</script>