<div id="add_modal" class="modal fade" role="dialog" data-backdrop="static">    <div class="modal-dialog">        <!-- Modal content-->        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h4><!-- <?php echo $this->lang->line('product_hsn_sac_lookup'); ?> -->                    Add Email Template                </h4>            </div>            <div class="modal-body">                <div id="loader">                    <h1 class="ml8"><span class="letters-container"> <span class="letters letters-left"><img src="<?php echo base_url('assets/'); ?>images/loader-icon.png" width="40px"></span></span><span class="circle circle-white"></span><span class="circle circle-dark"></span><span class="circle circle-container"><span class="circle circle-dark-dashed"></span></span></h1>                </div>                             <div class="row">                    <form id="templateForm">                        <div class="col-md-12">                            <div class="form-group">                                <label for="module_id">Select Module<span style="color:red;">*</span>                                </label>                                <select class="form-control select2" id="module_id_a" name="module_id_a" style="width: 100%;"></select> <span class="validation-color" id="err_module_id_a"><?php echo form_error('module_id_a'); ?></span>                            </div>                            <div class="form-group">                                <label for="email_template_name">Email Template Name<span style="color:red;">*</span>                                </label>                                <input type="text" class="form-control" id="email_template_name_a" name="email_template_name_a" value=""> <span class="validation-color" id="err_email_template_name_a"><?php echo form_error('email_template_name_a'); ?></span>                            </div>                            <div class="form-group">                                <label for="subject">Subject<span style="color:red;">*</span>                                </label>                                <input type="text" class="form-control" id="subject_a" name="subject_a" value=""> <span class="validation-color" id="err_subject_a"><?php echo form_error('subject_a'); ?></span>                            </div>                            <div class="form-group">                                <label for="message">Message<span style="color:red;">*</span>                                </label>                                <textarea class="form-control" id="message_a" rows="5" name="message_a"></textarea> <span class="validation-color" id="err_message_a"><?php echo form_error('message_a'); ?></span>                            </div>                            <div class="form-group">                                <label for="signature">Signature<span style="color:red;">*</span>                                </label>                                <textarea class="form-control" id="signature_a" rows="5" name="signature_a"></textarea> <span class="validation-color" id="err_signature_a"><?php echo form_error('signature_a'); ?></span>                            </div>                        </div>                                                           </form>                </div>            </div>            <div class="modal-footer">                <button type="submit" id="email_add_submit" class="btn btn-info pull-right" class="close">Add</button>            </div>        </div>    </div></div><script src="<?php echo base_url('assets/js/') ?>icon-loader.js"></script><script type="text/javascript">    $(document).ready(function () {        $(document).on("click", '.add_template', function (e) {            $.ajax({                url: base_url + "email_template/get_module_id",                type: "POST",                dataType: "json",                success: function (data)                {                    $('#module_id_a').html("");                    var option = "<option value=''>Select</option>";                    for (var i = 0; i < data.length; i++)                    {                        option += "<option value='" + data[i].module_id + "'>" + data[i].module_name + "</option>";                    }                    $('#module_id_a').html(option);                }            });        });        $("#email_add_submit").click(function (event) {            var module_id = $('#module_id_a').val();            var template_name = $('#email_template_name_a').val();            var subject = $('#subject_a').val();            var message = $('#message_a').val();            var signature = $('#signature_a').val();            if (module_id == null || module_id == "")            {                $("#err_module_id_a").text("Please select any module");                return false;            } else            {                $('#err_module_id_a').text("");            }            if (template_name == null || template_name == "")            {                $("#err_email_template_name_a").text("Please enter email template name");                return false;            } else            {                $('#err_email_template_name_a').text("");            }            if (subject == null || subject == "")            {                $("#err_subject_a").text("Please enter subject");                return false;            } else            {                $('#err_subject_a').text("");            }            if (message == null || message == "")            {                $("#err_message_a").text("Please enter message");                return false;            } else            {                $("#err_message_a").text("");            }            if (signature == null || signature == "")            {                $("#err_signature_a").text("Please enter signature");                return false;            } else            {                $('#err_signature_a').text("");            }            $.ajax({                url: base_url + "email_template/add_email_template/",                type: "POST",                dataType: "json",                data: {module_id: $("#module_id_a").val(),                email_template_name: $("#email_template_name_a").val(),                 subject: $("#subject_a").val(),                 message: $("#message_a").val(),                 signature: $("#signature_a").val()},                beforeSend: function(){                     // Show image container                    $("#loader").show();                },                 success: function (data) {                    if(data.flag){                                             dTable.destroy();                        dTable = getAllEmailTemplate();                        alert_d.text = data.msg;                        PNotify.success(alert_d);                                               $('#templateForm').trigger("reset");                        $("#loader").hide();                        $("#add_modal").modal('hide');                                              }else{                                               alert_d.text = data.msg;                        PNotify.error(alert_d);                                            }                }            });            anime.timeline({loop:!0}).add({targets:".ml8 .circle-white",scale:[0,3],opacity:[1,0],easing:"easeInOutExpo",rotateZ:360,duration:8e3}),anime({targets:".ml8 .circle-dark-dashed",rotateZ:360,duration:8e3,easing:"linear",loop:!0});        });        $("#message_a").wysihtml5();        $("#signature_a").wysihtml5();    });</script>