<style type="text/css">    textarea {    height: 28px;    width: 400px;}#textarea,#textarea1 {    -moz-appearance: textfield-multiline;    -webkit-appearance: textarea;    border: 1px solid gray;    resize: vertical !important;    font: medium -moz-fixed;    font: -webkit-small-control;    height: 100px;    overflow: auto;    padding: 2px;    resize: both;    width: 570px;    background: rgba(0,0,0,0.05);}</style><div id="view_modal" class="modal fade" role="dialog" data-backdrop="static">    <div class="modal-dialog">        <!-- Modal content-->        <div class="modal-content">            <div class="modal-header">                <button type="button" class="close" data-dismiss="modal">&times;</button>                <h4><!-- <?php echo $this->lang->line('product_hsn_sac_lookup'); ?> -->                    Email Template Details                </h4>            </div>            <div class="modal-body">                <div class="row">                    <form id="templateForm_v">                        <div class="col-md-12">                            <div class="form-group">                                <label for="module_id">Select Module<span style="color:red;">*</span></label>                                <select class="form-control select2" id="module_id_v" name="module_id_v" style="width: 100%;" readonly>                                </select>                                <span class="validation-color" id="err_module_id_v"><?php echo form_error('module_id_v'); ?></span>                            </div>                            <div class="form-group">                                <label for="email_template_name">Email Template Name<span style="color:red;">*</span></label>                                <input type="text" class="form-control" id="email_template_name_v" name="email_template_name_v" value="" readonly>                                <span class="validation-color" id="err_email_template_name_v"><?php echo form_error('email_template_name_v'); ?></span>                            </div>                            <div class="form-group">                                <label for="subject">Subject<span style="color:red;">*</span></label>                                <input type="text" class="form-control" id="subject_v" name="subject_v" value="" readonly>                                <span class="validation-color" id="err_subject_v"><?php echo form_error('subject_v'); ?></span>                            </div>                            <div class="form-group">                                <label for="message">Message<span style="color:red;">*</span></label>                                <div id="textarea" contentEditable='false' unselectable='true'></div>                                <span class="validation-color" id="err_message_v"><?php echo form_error('message_v'); ?></span>                            </div>                            <!-- <div class="form-group">                                <label for="message">Message<span style="color:red;">*</span></label>                                <textarea class="form-control" id="message_v" rows="5" name="message_v" readonly></textarea>                                <span class="validation-color" id="err_message_v"><?php echo form_error('message_v'); ?></span>                            </div> -->                            <div class="form-group">                                <label for="signature">Signature<span style="color:red;">*</span></label>                                <div id="textarea1" contentEditable='false' unselectable='true'></div>                                <span class="validation-color" id="err_signature_v"><?php echo form_error('message_v'); ?></span>                            </div>                            <!-- <div class="form-group">                                <label for="signature">Signature<span style="color:red;">*</span></label>                                <textarea class="form-control" id="signature_v" rows="5" name="signature_v" readonly></textarea>                                <span class="validation-color" id="err_signature_v"><?php echo form_error('signature_v'); ?></span>                            </div> -->                        </div>                                          </form>                </div>            </div>        </div>    </div></div><script type="text/javascript">    $(document).ready(function ()    {        var id = 0;        $(document).on("click", '.view_template', function (e) {            id = $(this).data('id');            $.ajax({                url: base_url + "email_template/get_email_template",                type: "POST",                data: {id: id},                dataType: "json",                success: function (data)                {                    $('#module_id_e').html("");                    for (var i = 0; i < data['module'].length; i++)                    {                        if (data['module'][i].module_id == data['data'][0].module_id)                        {                            option = "<option value='" + data['module'][i].module_id + "' selected>" + data['module'][i].module_name + "</option>";                        }                    }                    $('#module_id_v').html(option);                    $('#email_template_name_v').val(data['data'][0].email_template_name);                    $('#subject_v').val(data['data'][0].subject);                    var message = data['data'][0].message.replace(/\n|\\n|\r\n|\\r\\n/g, "\n");                    $('#message_v').val(message);                    $('#textarea').html(message);                    var signature = data['data'][0].signature.replace(/\n|\\n|\r\n|\\r\\n/g, "\n");                    $('#signature_v').val(signature);                    $('#textarea1').html(signature);                }            });        });    });</script>