<div class="modal fade" id="add_fy" tabindex="-1" role="dialog" aria-labelledby="" aria-hidden="true">    <div class="modal-dialog modal-dialog-centered">        <div class="modal-content">            <div class="modal-header">            	<button type="button" class="close" data-dismiss="modal" aria-label="Close">                <span aria-hidden="true">&times;</span>                </button>                 <h4 class="modal-title">Add Financial Year</h4>                             </div>            <div class="modal-body">                               <div class="row">                    <div class="col-sm-6">                        <div class="form-group">                            <label>From Date<span class="validation-color">*</span></label>                            <!-- <div class="input-group datepicker">                                <input type="text" class="form-control" name="year_from_date" id="form_date" readonly="">                                <span class="input-group-addon"><span class="fa fa-calendar"></span></span>                            </div> -->                                                        <div class="input-group date datepicker">                                    <input type="text" class="form-control" name="year_from_date" readonly="">                                    <div class="input-group-addon">                                            <span class="fa fa-calendar"></span>                                    </div>                            </div>                        </div>                    </div>                    <div class="col-sm-6">                        <div class="form-group">                            <label>To Date<span class="validation-color">*</span></label>                            <input type="text" class="form-control" name="year_to_date" readonly>                        </div>                    </div>                    <div class="col-sm-12">                        <div class="form-group">                            <div class="row">                                <div class="col-sm-5">                                    <label>Is it current Financial Year..?<span class="validation-color">*</span></label>                                </div>                                <div class="col-sm-2">                                    <div class="form-check">                                        <label class="form-check-label">                                            <input type="radio" class="form-check-input" name="is_current" id="1" value="1" checked="">                                            Yes <i class="input-helper"></i></label>                                    </div>                                </div>                                <div class="col-sm-2">                                    <div class="form-check">                                        <label class="form-check-label">                                            <input type="radio" class="form-check-input" name="is_current" id="0" value="0">                                            No <i class="input-helper"></i>                                        </label>                                    </div>                                </div>                            </div>                        </div>                    </div>                </div>                            </div>            <div class="modal-footer">                <button type="submit" class="btn btn-primary tbl-btn" id="addNewYear">                    Add                </button>                <button type="button" class="btn btn-primary tbl-btn" data-dismiss="modal">                    Cancel                </button>            </div>        </div>    </div></div>